mapper.select:
select t.*, p.name as projectName, u.username, case when trim(u.nick_name) = '' then u.username else u.nick_name end as nickname, v.version as flinkVersion from t_flink_app t inner join t_user u on t.user_id = u.user_id left join t_flink_env v on t.version_id = v.id left join t_flink_project p on t.project_id = p.id
t.team_id = #{application.teamId}
and t.job_type = #{application.jobType}
and t.job_type in
#{item}
and t.execution_mode = #{application.executionMode}
and t.job_name like concat('%', #{application.jobName},'%')
and t.job_name like '%' || #{application.jobName} || '%'
and p.name like concat('%', #{application.projectName},'%')
and p.name like '%' || #{application.projectName} || '%'
and t.state = #{application.state}
and t.user_id = #{application.userId}
and t.state in
#{item}
and t.tags like concat('%', #{application.tags},'%')
and t.tags like '%' || #{application.tags} || '%'

mapper.update:
update t_flink_app
set job_manager_url = #{url}
where id = #{id}

mapper.select:
select k8s_namespace
from (
    select k8s_namespace, max(create_time) as ct
    from t_flink_app
    where k8s_namespace is not null
    group by k8s_namespace
    order by ct desc
) as ns
limit #{limitSize}

select cluster_id
from (
    select c.cluster_id, max(a.create_time) as ct
    from t_flink_app a inner join t_flink_cluster c
    on a.flink_cluster_id = c.id
    where a.execution_mode = 5
    group by c.cluster_id
    order by ct desc
) as ci
limit #{limitSize}

select flink_image
from (
    select flink_image, max(create_time) as ct
    from t_flink_app
    where flink_image is not null
    and execution_mode = 6
    group by flink_image
    order by ct desc
) as fi
limit #{limitSize}
